descriptor.templateCss:
.radial-container {
    width: 100%;
    height: 100%;
    margin: 0;
    padding: 0;
    background: #FFFFFF;
}

.switch{
    font-size: 1.5rem;
}

h3{
    margin:0px;
    padding: 0px;
    font-size: 1.2rem;
    font-weight: 500;
}

p{
    margin:0px;
    padding: 0px;
    font-size: 0.8rem;
    font-weight: 500;
    color:#B6C3DB;
}

.header{
    display: flex;
    justify-content: space-between;
}

.parent-container {
    display: flex;
    padding: 10px;
    flex-direction: column;
    height: 100%; 
}

.iframe-wrapper {
    flex: 1 1 auto;
    display: flex;
    justify-content: center;
    margin-top: 0px;
}

.responsive-iframe {
    flex: 1 1 auto;
    width: 100%;
    height: 100%;
    border: 0;
}

.center {
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
}

.slider {
    width: 300px;
    height: 300px;
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    user-select: none;
}

.knob {
    width: 220px;
    height: 220px;
    background: #F4F5F7;
    border-radius: 50%;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    display: flex;
    align-items: center;
    justify-content: center;
}

.knob .rotator {
    width: 180px;
    height: 180px;
    background: #FFFFFF;
    border-radius: 50%;
    /*box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2), inset 1px 2px 1px white,*/
    /*    2px -1px 1px rgba(0, 0, 0, 0.2), -1px 0px 1px rgba(0, 0, 0, 0.2);*/
    display: flex;
    align-items: center;
    justify-content: center;
    position: relative;
    z-index: 1;
}

.knob .text {
    /*width: 40px;*/
    /*height: 40px;*/
    /*margin: 0 15px;*/
    font-family: Tahoma;
    font-size: 3rem;
    font-weight: 600;
    color: #3D8AFE;
    /*border: 0.3mm solid rgba(57, 55, 53);*/
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
}

.pointer {
    position: absolute;
    width: 20px;
    height: 20px;
    top: calc(50% - 10px);
    left: 0;
    display: flex;
    align-items: center;
    justify-content: flex-start;
    transform-origin: 110px 10px;
    transform: rotate(-45deg);
    color: #3E87F9;
}

.pointer span {
    font-size: 60px;
    transform: rotate(180deg) translateX(15px);
}

circle {
    fill: none;
}

circle:nth-child(1) {
    stroke: #F4F5F9;
    stroke-width: 15px;
    stroke-dasharray: 660;
    transform-origin: center;
    transform: rotate(135deg);
    stroke-linecap: round;
}

circle:nth-child(2) {
    stroke: rgb(2, 117, 232);
    stroke-width: 15px;
    stroke-dasharray: 880;
    stroke-dashoffset: calc(880 - (660 * 0) / 100);
    transform-origin: center;
    transform: rotate(135deg);
    stroke-linecap: round;
}

