self.onInit = function() {
    self.ctx.$scope.data = self.ctx.defaultSubscription.data;
    self.ctx.$scope.deviceTitle = self.ctx.settings.deviceTitle;
    self.ctx.$scope.deviceSubtitle = self.ctx.settings.deviceSubtitle;
    self.ctx.$scope.deviceName = self.ctx.settings.deviceName;
    self.ctx.$scope.entityId = self.ctx.settings.entityId;
    self.ctx.$scope.deviceStatus5 = false;
    self.ctx.$scope.deviceValue5 = 0;
    self.ctx.$scope.rpcMethod = self.ctx.settings.rpcMethod;
    self.ctx.$scope.rpcType = self.ctx.settings.rpcType;
    self.ctx.$scope.timeout = self.ctx.settings.requestTimeout;
    self.ctx.$scope.checkState = false;

    if (!self.ctx.$scope.deviceTitle) { self.ctx.$scope.deviceTitle = "Device Title" }
    if (!self.ctx.$scope.deviceSubtitle) { self.ctx.$scope.deviceSubtitle = "Device Subtitle" }
    if (!self.ctx.$scope.entityId) { self.ctx.$scope.entityId = "cde78cc0-2398-11ef-b19f-cdc6a3f105ff" }
    if (!self.ctx.$scope.deviceName) { self.ctx.$scope.deviceName = "device5" }
    if (!self.ctx.$scope.rpcMethod) { self.ctx.$scope.rpcMethod = "set_switch" }
    if (!self.ctx.$scope.rpcType) { self.ctx.$scope.rpcType = "oneway" }
    if (!self.ctx.$scope.timeout) { self.ctx.$scope.timeout = 5000 }

    let knob = document.querySelector(".knob");
    let circle = document.getElementById("circle2");
    let pointer = document.querySelector(".pointer");
    let text = document.querySelector(".text");

    let isRotating = false;
    let changeTimeout;

    document.addEventListener("mousedown", (e) => {
        if (e.target.closest(".knob")) {
            isRotating = true;
        }
    });

    const rotateKnob = (e) => {
        if (isRotating) {
            let knobX = knob.getBoundingClientRect().left + knob.clientWidth / 2;
            let knobY = knob.getBoundingClientRect().top + knob.clientHeight / 2;

            let deltaX = e.clientX - knobX;
            let deltaY = e.clientY - knobY;

            let angleRad = Math.atan2(deltaY, deltaX);
            let angleDeg = (angleRad * 180) / Math.PI;

            let rotationAngle = (angleDeg - 135 + 360) % 360;

            if (rotationAngle <= 270) {
                pointer.style.transform = `rotate(${rotationAngle - 45}deg)`;

                let progressPercent = rotationAngle / 270;
                circle.style.strokeDashoffset = `${880 - 660 * progressPercent}`;

                let newValue = Math.round(progressPercent * 100);
                text.innerHTML = `${newValue}`;

                clearTimeout(changeTimeout);
                changeTimeout = setTimeout(() => {
                    console.log(`Manual value change: ${newValue}`);
                    self.ctx.$scope.deviceValue5 = newValue;
                    self.ctx.$scope.sendCommand(newValue);
                }, 300);
            }
        }
    };

    document.addEventListener("mousemove", rotateKnob);
    document.addEventListener("mouseup", () => {isRotating = false});

    self.ctx.$scope.updateKnobAndProgressBar = (value) => {
        let startValue = 0;
        let endValue = value;
        let duration = 500;
        let startTime = null;

        const animateValue = (timestamp) => {
            if (!startTime) startTime = timestamp;
            let progress = timestamp - startTime;
            let currentValue = Math.min(startValue + (endValue - startValue) * (progress / duration), endValue);
            let progressPercent = currentValue / 100;
            let rotationAngle = progressPercent * 270;
            pointer.style.transform = `rotate(${rotationAngle - 45}deg)`;
            circle.style.strokeDashoffset = `${880 - 660 * progressPercent}`;
            text.innerHTML = `${Math.round(currentValue)}`;
            if (progress < duration) {
                requestAnimationFrame(animateValue);
            }
        };

        requestAnimationFrame(animateValue);
    };
    
    self.ctx.$scope.sendCommand = function(status) {
        console.log('send RPC');
        console.log('rpcMethod', self.ctx.$scope.rpcMethod);
        console.log('timeout', self.ctx.$scope.timeout);
        console.log('status', status);  // Agregar este log
        var rpcParams = {
            switch: status,
        };

        var oneWayElseTwoWay = self.ctx.$scope.rpcType === "oneway";

        var commandObservable;
        if (oneWayElseTwoWay) {
            console.log("oneway");
            console.log(JSON.stringify(rpcParams));
            commandObservable = self.ctx.controlApi.sendOneWayCommand(self.ctx.$scope.rpcMethod, rpcParams, self.ctx.$scope.timeout);
        } else {
            console.log(rpcParams);
            console.log("twoway");
            commandObservable = self.ctx.controlApi.sendTwoWayCommand(self.ctx.$scope.rpcMethod, rpcParams, self.ctx.$scope.timeout);
        }

        commandObservable.subscribe(
            function(response) {
                if (oneWayElseTwoWay) {
                    self.ctx.$scope.rpcCommandResponse = "Command was successfully received by device.<br> No response body because of one way command mode.";
                    console.log(self.ctx.$scope.rpcCommandResponse);
                } else {
                    self.ctx.$scope.rpcCommandResponse = "Response from device:<br>" + JSON.stringify(response, undefined, 2);
                    console.log(self.ctx.$scope.rpcCommandResponse);
                }
                self.ctx.detectChanges();
            },
            function(rejection) {
                self.ctx.$scope.rpcCommandResponse = "Failed to send command to the device:<br>Status: " + rejection.status + "<br>Status text: '" + rejection.statusText + "'";
                console.log(self.ctx.$scope.rpcCommandResponse);
                self.ctx.detectChanges();
            }
        );
    };
    
