    self.ctx.$scope.gpioToggleChange = function($event) {
        
        // self.ctx.$scope.sendCommand($event.target.checked);
        
        self.ctx.attributeService.saveEntityAttributes(
            { entityType: 'DEVICE', id: self.ctx.$scope.entityId },
            'SHARED_SCOPE', [{
                key: "deviceState5",
                value: $event.target.checked
            }]
        ).subscribe((res) => {
            console.log(res);
        });
    };
    
    const datasources = [{
        type: "entity",
        dataKeys: [{
            decimals: 0,
            label: "",
            name: "deviceState5",
            settings: {},
            type: "attribute"
        }, {
            decimals: 0,
            label: "",
            name: "deviceValue5",
            settings: {},
            type: "attribute"
        }],
        entityFilter: {
            type: "singleEntity",
            singleEntity: {
                id: self.ctx.$scope.entityId,
                entityType: "DEVICE"
            }
        }
    }];

    const subscriptionOptions = {
        type: 'latest',
        datasources: datasources,
        callbacks: {
            onDataUpdated: () => {
                self.onDataUpdated();
            }
        }
    };

    self.ctx.subscriptionApi.createSubscription(subscriptionOptions, true).subscribe((subscription) => {
        // self.ctx.defaultSubscription = subscription;
