        // self.ctx.datasources = subscription.datasources;
    });
};

self.onDataUpdated = function() {
    try {
        const device5 = JSON.parse(self.ctx.data[0].data[0][1]);
        self.ctx.$scope.deviceStatus5 = device5;
    } catch (e) {
        console.error('Error parsing device5', e);
    }

    try {
        const value5 = self.ctx.data[1].data[0][1];
        self.ctx.$scope.deviceValue5 = value5;
        self.ctx.$scope.updateKnobAndProgressBar(value5);
    } catch (e) {
        console.error('Error parsing value5', e);
    }

    if (self.ctx.$scope.deviceName === 'device5') {
        self.ctx.$scope.checkState = self.ctx.$scope.deviceStatus5;
    }

    self.ctx.detectChanges();
};

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "deviceTitle": {
                "title": "Device Title",
                "type": "string",
                "default": "Device Title"
            },
            "deviceSubtitle": {
                "title": "Device Subtitle",
                "type": "string",
                "default": "Device Subtitle"
            },
            "deviceName": {
                "title": "Device Name",
                "type": "string",
                "default": "DeviceName"
            },
            "entityId": {
                "title": "Entity Id",
                "type": "string",
                "default": "entityId"
            },
            "rpcMethod": {
                "title": "Rpc Method",
                "type": "string",
                "default": "rpcMethod"
            },
            "rpcType": {
                "title": "Rpc Type",
                "type": "string",
                "enum": [
                    "oneway",
                    "twoway"
                ]
            },
            "requestTimeout": {
                "title": "Request Timeout",
                "type": "number",
                "default": "0"
            },
            "required": []
        },
        "form": [
            "deviceTitle",
            "deviceSubtitle",
            "deviceName",
            "rpcMethod",
            "rpcType",
            "requestTimeout",
            "entityId"
        ]
    }
}
